import { Component, OnInit, ViewChild, OnDestroy } from "@angular/core";
import { MatPaginator } from "@angular/material/paginator";
import { MatTableDataSource } from "@angular/material/table";
import { Observable, Subscription } from "rxjs";
import { bookData } from "src/app/interfaces/istudent-book";
import { StudentNewsService } from "src/app/services/student-news.service";
import { INews } from "./inews";
import { IstudentNews } from "./istudent-news";

@Component({
  selector: "app-student-news",
  templateUrl: "./student-news.component.html",
  styleUrls: ["./student-news.component.scss"],
})
export class StudentNewsComponent implements OnInit, OnDestroy {
  @ViewChild(MatPaginator) paginator: MatPaginator;

  news = {} as INews;
  booksData: Subscription;
  newsFeed = [] as IstudentNews[];
  booksFeed: bookData[];
  obs: Observable<bookData[]>;
  searchKey: boolean = false;
  filter: string;
  constructor(private newsService: StudentNewsService) {}
  dataSource: MatTableDataSource<bookData> = new MatTableDataSource();

  getBooks() {
    this.booksData = this.newsService.getBooks().subscribe(
      (data) => {
        this.booksFeed = data.data;
        this.dataSource = new MatTableDataSource(data.data);
        this.dataSource.paginator = this.paginator;
        this.obs = this.dataSource.connect();
        console.log(this.dataSource);
      },
      (err) => {
        console.log(err);
      }
    );
  }

  getEventValue($event: any): string {
    return ($event.target as HTMLInputElement).value;
  }

  applyFilter(filterValue: string) {
    this.dataSource.filter = filterValue.trim().toLowerCase();
    if (this.dataSource.filter) {
      this.searchKey = true;
    }
  }

  onSearchClear() {
    this.filter = "";
    this.searchKey = false;
    this.dataSource.filter = "";
  }

  ngOnInit(): void {
    this.getBooks();
    console.log(this.obs);
  }

  ngOnDestroy(): void {
    this.booksData.unsubscribe();
  }
}
<div class="container-fluid">
  <div class="row mt-3">
    <div class="col-12">
      <div class="search-div">
        <mat-form-field class="search-form-field" floatLabel="never">
          <input
            matInput
            (keyup)="applyFilter(getEventValue($event))"
            placeholder="Cautare dupa titlu"
            autocomplete="off"
            [(ngModel)]="filter"
          />
          <button
            mat-button
            matSuffix
            mat-icon-button
            aria-label="Clear"
            *ngIf="searchKey"
            (click)="onSearchClear()"
          >
            <mat-icon>close</mat-icon>
          </button>
        </mat-form-field>
      </div>
    </div>
  </div>
  <div class="row">
    <div
      class="col-lg-3 col-md-4 col-sm-12 mt-3"
      *ngFor="let book of obs | async"
    >
      <mat-card class="example-card align-self-center">
        <mat-card-header>
          <div
            mat-card-avatar
            [ngStyle]="{'background': 'url('+ book.prodCoverUrl +') no-repeat center', 'background-size': 'cover'}"
          ></div>
          <mat-card-title
            class="noOverflow"
            [ngClass]="{'noAuthors': !book.autori}"
            >{{book.prodNume}}</mat-card-title
          >
          <mat-card-subtitle *ngIf="book.autori"
            >{{book.autori[0].autNumeFull}}</mat-card-subtitle
          >
        </mat-card-header>
        <img
          mat-card-image
          [src]="book.prodCoverUrl"
          [alt]="book.prodNume"
          class="image-card"
        />
        <mat-card-content>
          <p class="p-content">{{book.prodDescriere}}</p>
        </mat-card-content>
        <mat-card-actions>
          <button mat-button>LIKE</button>
          <button mat-button>SHARE</button>
        </mat-card-actions>
      </mat-card>
    </div>
  </div>
  <div class="container-fluid w-100 mt-3">
    <mat-paginator
      [pageSizeOptions]="[12, 24, 48]"
      aria-label="Select page"
      [pageSize]="12"
    ></mat-paginator>
  </div>
</div>
